void selectLanguage() {
	FixConsoleWindow();
	DrawBound();
	system("Color F0");
	char choice;
	GotoXY(42, 15);
	std::cout << "Select Language / Chon Ngon Ngu: (E) English / (V) Vietnamese: ";
	std::cin >> choice;

	if (choice == 'E' || choice == 'e') {
		currentLanguage = VIETNAMESE;
	}
	else if (choice == 'V' || choice == 'v') {
		currentLanguage = ENGLISH;
	}
	else {
		std::cout << "Invalid choice. Defaulting to English.\n";
		currentLanguage = VIETNAMESE;
	}
	ToggleLanguage();
	system("cls");
}
